    private void SearchBtnActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_SearchBtnActionPerformed
        // TODO add your handling code here:
        boolean found=false;
		while(!found){
			for(Transaction t: Transaction){
				if(t.getPhone().equals(searchnum.getText())){
					DisplayTA.setText(t.toString());
					found=true;
					break;
				}
			}
		}
		if(found==false){
			JOptionPane.showMessageDialog(null,"Transaction not found","Error!",JOptionPane.ERROR_MESSAGE);
		}
    }//GEN-LAST:event_SearchBtnActionPerformed

    private void DeleteBtnActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_DeleteBtnActionPerformed
        // TODO add your handling code here:
        boolean found=false;
		while(!found){
			for(Transaction t: Transaction){
				if(t.getPhone().equals(deletenum.getText())){
					DisplayTA.setText(t.toString());
					found=true;
                                        Transaction.remove(t);
					break;
				}
			}
		}
		if(found==false){
			JOptionPane.showMessageDialog(null,"Transaction not found","Error!",JOptionPane.ERROR_MESSAGE);
		}
    }//GEN-LAST:event_DeleteBtnActionPerformed

