    private void ToCurrencyActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_ToCurrencyActionPerformed
        // TODO add your handling code here:
        try{
			String name=(String)NameTF.getText();
			String phone=(String)PhoneTF.getText();
			String ID=(String)ICTF.getText();
			String address=(String)AddressTF.getText();
			String fromC = (String)FromCurrency.getSelectedItem();
			String toC = (String)ToCurrency.getSelectedItem();
			double from = Double.parseDouble(FromValue.getText());
			
			if(from<0){
				throw new Exception();
			}
			Transaction transaction = new Transaction(name,phone,ID,address,fromC,toC,from);
			ToValue.setText(String.format("%.4f",transaction.setConvertedValue()));
		}
		catch(NumberFormatException e){
			JOptionPane.showMessageDialog(null,"Error: Enter valid integer ONLY!","Message",JOptionPane.INFORMATION_MESSAGE);
		}
		catch(Exception e){
			JOptionPane.showMessageDialog(null,"Value should be more than 0","Error!",JOptionPane.ERROR_MESSAGE);
		}
    }//GEN-LAST:event_ToCurrencyActionPerformed

    private void AddTransactionBtnActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_AddTransactionBtnActionPerformed
        // TODO add your handling code here:
        try{
			if(NameTF.getText().isEmpty()||PhoneTF.getText().isEmpty()||ICTF.getText().isEmpty()||AddressTF.getText().isEmpty()||FromCurrency.getSelectedItem()==null||ToCurrency.getSelectedItem()==null||FromValue.getText().isEmpty()){
				throw new Exception();
			}
			String name=NameTF.getText();
			String phone=PhoneTF.getText();
			String ID=ICTF.getText();
			String address=AddressTF.getText();
			String fromC=(String)FromCurrency.getSelectedItem();
			String toC=(String)ToCurrency.getSelectedItem();
			double from=Double.parseDouble(FromValue.getText());
			double to=Double.parseDouble(ToValue.getText());
						
			Transaction.add(new Transaction(name,phone,ID,address,fromC,toC,from));
			JOptionPane.showMessageDialog(null,"Added to Array List");
		}
		catch(Exception e){
			JOptionPane.showMessageDialog(null,"Error : Enter valid data","Error",JOptionPane.ERROR_MESSAGE);
		}
    }//GEN-LAST:event_AddTransactionBtnActionPerformed

    private void DisplayTransactionBtnActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_DisplayTransactionBtnActionPerformed
        // TODO add your handling code here:
        String name=(String)NameTF.getText();
		String phone=(String)PhoneTF.getText();
		String ID=(String)ICTF.getText();
		String address=(String)AddressTF.getText();
		String fromC = (String)FromCurrency.getSelectedItem();
		String toC = (String)ToCurrency.getSelectedItem();
		double from = Double.parseDouble(FromValue.getText());
			
		Transaction transaction = new Transaction(name,phone,ID,address,fromC,toC,from);
		DisplayTA.setText(transaction.toString());
    }//GEN-LAST:event_DisplayTransactionBtnActionPerformed

    private void DisplayAllTransactionBtnActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_DisplayAllTransactionBtnActionPerformed
        // TODO add your handling code here:
        try{
			File inFile = new File("Transactions.txt");
			FileReader fr = new FileReader(inFile);
			BufferedReader br = new BufferedReader(fr);
			DisplayTA.read(br,null);
			br.close();
			DisplayTA.requestFocus();
		}
		catch(IOException e){
			JOptionPane.showMessageDialog(null,"Error writing to file","Error!",JOptionPane.ERROR_MESSAGE);
		}
    }//GEN-LAST:event_DisplayAllTransactionBtnActionPerformed

    private void ClearBtnActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_ClearBtnActionPerformed
        // TODO add your handling code here:
        DisplayTA.setText("");
    }//GEN-LAST:event_ClearBtnActionPerformed

    private void SaveTransactionBtnActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_SaveTransactionBtnActionPerformed
        // TODO add your handling code here:
        try{
			FileWriter fw = new FileWriter("Transactions.txt");
			PrintWriter pw = new PrintWriter(fw);
			for(Transaction t: Transaction){
				pw.write(t.toString()+"\n\n");
			}
			fw.close();
			JOptionPane.showMessageDialog(null,"Successfully written to file","Done!",JOptionPane.INFORMATION_MESSAGE);
		}
		catch(IOException e){
			JOptionPane.showMessageDialog(null,"Error writing to file","Error!",JOptionPane.ERROR_MESSAGE);
		}
    }//GEN-LAST:event_SaveTransactionBtnActionPerformed

    private void DisplayTodaysTransactionBtnActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_DisplayTodaysTransactionBtnActionPerformed
        // TODO add your handling code here:
        for(Transaction t:Transaction){
            DisplayTA.append(t.toString()+"\n\n");
		}
    }//GEN-LAST:event_DisplayTodaysTransactionBtnActionPerformed

